/*************************** Passport customize function ***************************/
passport.use(new BasicStrategy(
    function (username, password, done) {
        Citizen.findOne({ username: username }, function (err, user) {
            var sendPassword = password.hashCode();
            if (err) { return done(err); }
            console.log(user);
            console.log("Sent: " + sendPassword);
            if (!user && !sendPassword) { return done(null, false); }
            return done(null, user);
        });
    }
));

